    @Test
    public void TestDone() {
        Reminder r = new Reminder((long)1, "Esse é um teste");

        r.setDate("31/01/2000");
        r.setHour("00:00");

        r.setDone(1);
        assertTrue(r.isDone());

        r.setDone(0);
        assertFalse(r.isDone());

    }

    @Test
    public void TestHoraInvalida() {
        Reminder r = new Reminder((long)1, "Esse é um teste");

        r.setDate("31/01/2000");
        r.setHour("23:59");

        assertTrue(r.isValid());

        r.setHour("24:00");
        assertTrue(r.isValid());
    }



