    @Test
    public void TestDatas() {
        Reminder r = new Reminder((long)1, "Esse é um teste");

        r.setHour("00:00");
        for (int a = 1900; a < 2100; a++){
            for (int m = 1; m < 13; m++){
                for(int d = 1; d < 32; d++){
                    if (d < 10 && m < 10) r.setDate("0" + d + "/0" + m + "/" + a);
                    if (d < 10 && m > 10) r.setDate("0" + d + "/" + m + "/" + a);
                    if (d > 10 && m < 10) r.setDate(d + "/0" + m + "/" + a);
                    if (d > 10 && m > 10) r.setDate(d + "/" + m + "/" + a);

                    assertTrue(r.isValid());
                }
            }
        }
    }



